(93, 'product_creation_usability_test_12_months', 'survey', 'en_US', 'Try the new product edit form prototype', 'Take an early look at the future of editing products in WooCommerce. Share your thoughts (~5 minutes) and help shape the experience for yourself and other merchants.', '{}', 'pending', 'woocommerce.com', '2022-09-06 21:53:30', NULL, 0, 'plain', '', 0, 'info', 0),
(94, 'custom_deposit_schedules_inbox', 'marketing', 'en_US', 'Customize your deposit schedule', 'Receive your funds according to your own business needs with custom deposit schedules from WooCommerce Payments. You’ll have the flexibility to deposit funds into your bank account daily, weekly, monthly, or even on-demand – you’ve done the work, now get paid on your terms.', '{}', 'pending', 'woocommerce.com', '2022-09-15 20:18:14', NULL, 0, 'plain', '', 0, 'info', 0),
(95, 'wc-admin-edit-products-on-the-move', 'info', 'en_US', 'Edite produtos de qualquer lugar', 'Edite e crie novos produtos do seu dispositivo móvel com o aplicativo Woo', '{}', 'unactioned', 'woocommerce-admin', '2022-09-22 19:28:33', NULL, 0, 'plain', '', 0, 'info', 0),
(96, 'googlelistings_signals2022_hasGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-23 19:29:16', NULL, 0, 'plain', '', 0, 'info', 0),
(97, 'googlelistings_signals2022_noGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-23 19:29:16', NULL, 0, 'plain', '', 0, 'info', 0),
(98, 'pre-black-friday-sale-2022-announcement', 'marketing', 'en_US', 'Surprise! We’re offering 40% off everything at WooCommerce.com', 'Just in time to get your store Black Friday ready. Sale ends 7 October, 2pm UTC.', '{}', 'unactioned', 'woocommerce.com', '2022-10-03 19:15:48', NULL, 0, 'plain', '', 0, 'info', 0),
(99, 'shipping_category_q4_2022', 'marketing', 'en_US', 'Save time on shipping', 'Is your store all set to ship? Save valuable time (and money!) by automating your fulfillment process for the busiest shopping season. Explore our range of trusted shipping partners to get started.', '{}', 'unactioned', 'woocommerce.com', '2022-10-13 19:41:32', NULL, 0, 'plain', '', 0, 'info', 0),
(100, 'facebook_q4-2022_hasFB', 'marketing', 'en_US', 'Drive more demand this holiday season with Facebook for WooCommerce', 'Deliver a personalized and seamless shopping experience this holiday season with Facebook for WooCommerce. With more ad formats than ever, you can create engaging ads that meet customers across Facebook, Instagram, Messenger, and WhatsApp throughout the busy shopping season.', '{}', 'pending', 'woocommerce.com', '2022-10-20 19:23:11', NULL, 0, 'plain', '', 0, 'info', 0),
(101, 'facebook_q4-2022_noFB', 'marketing', 'en_US', 'Meet your customers where they are with Facebook for WooCommerce', 'Don’t wait for your customers to come to you – meet them where they’re browsing! Use the Facebook for WooCommerce extension to promote your products on Facebook, Instagram, Messenger, and WhatsApp. Create seamless shopping experiences – straight from your WooCommerce dashboard.', '{}', 'unactioned', 'woocommerce.com', '2022-10-22 19:49:48', NULL, 0, 'plain', '', 0, 'info', 0),
(102, 'tiktok-targeted-q4-2022', 'marketing', 'en_US', 'Get $200 in ad credit from TikTok after you spend $20 on your first campaign', 'Reach one billion shoppers with TikTok for WooCommerce this holiday season! Sync your product catalog, capture insights, and create ad campaigns right from your dashboard. Connect your store today to unlock this limited time offer! <a href=\"https://ads.tiktok.com/help/article?aid=10011326\">Terms &amp; conditions apply</a>.', '{}', 'pending', 'woocommerce.com', '2022-10-25 19:37:31', NULL, 0, 'plain', '', 0, 'info', 0),
(103, 'paypal_paylater_g3_q4_22', 'marketing', 'en_US', 'Turn browsers into buyers with Pay Later', 'Add PayPal at checkout, plus give customers a buy now, pay later option from the name they trust. With Pay in 4 &amp; Pay Monthly, available in PayPal Payments, you get paid up front while letting customers spread their payments over time. Boost your average order value and convert more sales – at no extra cost to you.', '{}', 'pending', 'woocommerce.com', '2022-11-01 19:18:22', NULL, 0, 'plain', '', 0, 'info', 0),
(104, 'paypal_paylater_g2_q4_22', 'marketing', 'en_US', 'Upgrade to PayPal Payments to offer Pay Later at checkout', 'PayPal Pay Later is included in PayPal Payments at no additional cost to you. Customers can spread their payments over time while you get paid up front. \r\nThere’s never been a better time to upgrade your PayPal plugin. Simply download it and connect with a PayPal Business account.', '{}', 'pending', 'woocommerce.com', '2022-11-03 19:27:38', NULL, 0, 'plain', '', 0, 'info', 0),
(105, 'woocommerce_admin_deprecation_q4_2022', 'info', 'en_US', 'WooCommerce Admin is part of WooCommerce!', 'To make sure your store continues to run smoothly, check that WooCommerce is up-to-date – at least version 6.5 – and then disable the WooCommerce Admin plugin.', '{}', 'pending', 'woocommerce.com', '2022-11-04 19:21:43', NULL, 0, 'plain', '', 0, 'info', 0),
(106, 'google_listings_ads_custom_attribute_mapping_q4_2022', 'marketing', 'en_US', 'Our latest improvement to the Google Listings & Ads extension: Attribute Mapping', 'You spoke, we listened. This new feature enables you to easily upload your products, customize your product attributes in one place, and target shoppers with more relevant ads. Extend how far your ad dollars go with each campaign.', '{}', 'pending', 'woocommerce.com', '2022-11-30 19:31:45', NULL, 0, 'plain', '', 0, 'info', 0),
(107, 'wc-admin-manualauthcapture', 'marketing', 'en_US', 'Miss fewer manual payments in WooCommerce Payments', 'Using WooCommerce Payments to manually authorize and capture payments? We’ve introduced a simplified and more efficient process, so you can miss fewer payments and continue to grow your revenue.', '{}', 'pending', 'woocommerce.com', '2022-11-30 19:31:45', NULL, 0, 'plain', '', 0, 'info', 0),
(108, 'apple_pay_square_q422', 'marketing', 'en_US', 'Grow your business with Apple Pay', 'Start boosting your online conversions today with Apple Pay – shoppers can safely complete a purchase with a touch or a glance and are more likely to come back. Enable Apple Pay with Square by selecting  Settings &gt; Payments &gt; Square.', '{}', 'pending', 'woocommerce.com', '2022-12-07 19:32:29', NULL, 0, 'plain', '', 0, 'info', 0),
(109, 'apple_pay_wcpay_q422', 'marketing', 'en_US', 'Grow your business with Apple Pay', 'Start boosting your online conversions today with Apple Pay – shoppers can safely complete a purchase with a touch or a glance and are more likely to come back. Confirm Apple Pay is enabled by selecting Payments &gt; Settings and making sure Enable express checkouts is checked.', '{}', 'pending', 'woocommerce.com', '2022-12-07 19:32:29', NULL, 0, 'plain', '', 0, 'info', 0),
(110, 'meta-hasFB-dec-q4-2022', 'marketing', 'en_US', 'Power up your Facebook ad strategy', 'Did you know that new privacy policies may be decreasing the effectiveness of your Facebook ads? Get back on track with Meta’s Conversion API. It creates a direct connection between the marketing data from your store and Meta, helping you more accurately track, attribute and improve your Facebook marketing performance.', '{}', 'pending', 'woocommerce.com', '2022-12-09 19:15:21', NULL, 0, 'plain', '', 0, 'info', 0),
(111, 'meta-noFB-dec-q4-2022', 'marketing', 'en_US', 'Create highly targeted Facebook ads with Facebook for WooCommerce', 'Sync your product catalog to create Facebook ads and track their performance – all from within your WooCommerce dashboard. With improved tracking signals and speed, plus more accurate tracking with the introduction of Meta’s Conversion API, you’ll have all the tools you need to run effective ad campaigns.', '{}', 'unactioned', 'woocommerce.com', '2022-12-12 19:26:38', NULL, 0, 'plain', '', 0, 'info', 0);
